package org.springframework.ide.eclipse.boot.dash.model.actuator.env;

import org.springframework.ide.eclipse.beans.ui.live.model.DisplayName;

public class Property implements DisplayName {

	private final String name;
	private final String value;
	private final PropertyOrigin origin;

	public Property(String name, String value, PropertyOrigin origin) {
		this.name = name;
		this.value = value;
		this.origin = origin;
	}

	public String getName() {
		return name;
	}

	public String getValue() {
		return value;
	}

	public PropertyOrigin getOrigin() {
		return this.origin;
	}

	@Override
	public String getDisplayName() {
		return this.name + " = " + this.value;
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((name == null) ? 0 : name.hashCode());
		result = prime * result + ((origin == null) ? 0 : origin.hashCode());
		result = prime * result + ((value == null) ? 0 : value.hashCode());
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Property other = (Property) obj;
		if (name == null) {
			if (other.name != null)
				return false;
		} else if (!name.equals(other.name))
			return false;
		if (origin == null) {
			if (other.origin != null)
				return false;
		} else if (!origin.equals(other.origin))
			return false;
		if (value == null) {
			if (other.value != null)
				return false;
		} else if (!value.equals(other.value))
			return false;
		return true;
	}

}